sourcesContent:
// controllers/AppController.js
import { isAlive } from '../utils/redis';
import { isAlive as _isAlive, nbUsers, nbFiles } from '../utils/db';

const AppController  = {
    async getStatus(req, res) {
    const redisStatus = await isAlive();
    const dbStatus = await _isAlive();

    res.status(200).json({
      "redis": `${redisStatus}`,
      "db": `${dbStatus}`,
    });
  },

    async getStats(req, res) {
    const userCount = await nbUsers();
    const fileCount = await nbFiles();

    res.status(200).json({
      "users": `${userCount}`,
      "files": `${fileCount}`,
    });
  }
}

export default AppController;

